-- ----------------------------
-- Procedure structure for sp_menu
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_menu`;
delimiter ;;
CREATE PROCEDURE `sp_menu`(in v_id smallint, in lang varchar(12), in v_active tinyint)
BEGIN
		SELECT
		id,
		CASE lang
		WHEN 'english' THEN
		 menu_en
		WHEN 'spanish' THEN
		 menu_es
		WHEN 'french' THEN
		 menu_fr
		WHEN 'portuguese' THEN
		 menu_pt
		WHEN 'italian' THEN
		 menu_it
		WHEN 'deutsch' THEN
		 menu_de
		WHEN 'russian' THEN
		 menu_ru
		WHEN 'korean' THEN
		 menu_kr
		WHEN 'taiwan' THEN
		 menu_tw
		WHEN 'chinese' THEN
		 menu_cn
		END AS menu,
		url,
		submenu
		FROM menu
		WHERE (v_id IS NULL or id = v_id) AND (v_active IS NULL or active = v_active)
		ORDER BY id ASC;
	END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_news
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_news`;
delimiter ;;
CREATE PROCEDURE `sp_news`(IN v_id INT, IN lang VARCHAR(12))
BEGIN
	SELECT
	n.`id`,
	-- Title --
	CASE lang
	WHEN 'english' THEN
	 n.title_en
	WHEN 'spanish' THEN
	 n.title_es
	WHEN 'french' THEN
	 n.title_fr
	WHEN 'portuguese' THEN
	 n.title_pt
	WHEN 'italian' THEN
	 n.title_it
	WHEN 'deutsch' THEN
	 n.title_de
	WHEN 'russian' THEN
	 n.title_ru
	WHEN 'korean' THEN
	 n.title_kr
	WHEN 'taiwan' THEN
	 n.title_tw
	WHEN 'chinese' THEN
	 n.title_cn
	END AS title,
	-- Title / END --
	-- Sub Title --
	CASE lang
	WHEN 'english' THEN
	 n.subtitle_en
	WHEN 'spanish' THEN
	 n.subtitle_es
	WHEN 'french' THEN
	 n.subtitle_fr
	WHEN 'portuguese' THEN
	 n.subtitle_pt
	WHEN 'italian' THEN
	 n.subtitle_it
	WHEN 'deutsch' THEN
	 n.subtitle_de
	WHEN 'russian' THEN
	 n.subtitle_ru
	WHEN 'korean' THEN
	 n.subtitle_kr
	WHEN 'taiwan' THEN
	 n.subtitle_tw
	WHEN 'chinese' THEN
	 n.subtitle_cn
	END AS subtitle,
	-- Sub Title / END --
	-- Content --
	CASE lang
	WHEN 'english' THEN
	 n.content_en
	WHEN 'spanish' THEN
	 n.content_es
	WHEN 'french' THEN
	 n.content_fr
	WHEN 'portuguese' THEN
	 n.content_pt
	WHEN 'italian' THEN
	 n.content_it
	WHEN 'deutsch' THEN
	 n.content_de
	WHEN 'russian' THEN
	 n.content_ru
	WHEN 'korean' THEN
	 n.content_kr
	WHEN 'taiwan' THEN
	 n.content_tw
	WHEN 'chinese' THEN
	 n.content_cn
	END AS content,
	-- Content / END --
	n.`image`,
	u.ID as id_user,
	u.username,
	n.posted_by,
	n.posted_date,
	s.id as id_state,
	COUNT(nc.id) AS comments,
	s.state
	FROM
	news AS n
	INNER JOIN users AS u ON(u.`ID` = n.`posted_by`)
	INNER JOIN state AS s ON(s.`id` = n.`state`)
	LEFT JOIN `news_comments` AS nc ON (nc.`id_news` = n.`id`)
	WHERE (v_id IS NULL OR n.id = v_id) and n.state IN (1, 3)
	GROUP BY n.`id`
	ORDER BY n.`id` DESC
	limit 20 OFFSET 6;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_news_top
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_news_top`;
delimiter ;;
CREATE PROCEDURE `sp_news_top`(IN v_id INT, IN lang VARCHAR(12))
BEGIN
	SELECT
	n.`id`,
	-- Title --
	CASE lang
	WHEN 'english' THEN
	 n.title_en
	WHEN 'spanish' THEN
	 n.title_es
	WHEN 'french' THEN
	 n.title_fr
	WHEN 'portuguese' THEN
	 n.title_pt
	WHEN 'italian' THEN
	 n.title_it
	WHEN 'deutsch' THEN
	 n.title_de
	WHEN 'russian' THEN
	 n.title_ru
	WHEN 'korean' THEN
	 n.title_kr
	WHEN 'taiwan' THEN
	 n.title_tw
	WHEN 'chinese' THEN
	 n.title_cn
	END AS title,
	-- Title / END --
	-- Sub Title --
	CASE lang
	WHEN 'english' THEN
	 n.subtitle_en
	WHEN 'spanish' THEN
	 n.subtitle_es
	WHEN 'french' THEN
	 n.subtitle_fr
	WHEN 'portuguese' THEN
	 n.subtitle_pt
	WHEN 'italian' THEN
	 n.subtitle_it
	WHEN 'deutsch' THEN
	 n.subtitle_de
	WHEN 'russian' THEN
	 n.subtitle_ru
	WHEN 'korean' THEN
	 n.subtitle_kr
	WHEN 'taiwan' THEN
	 n.subtitle_tw
	WHEN 'chinese' THEN
	 n.subtitle_cn
	END AS subtitle,
	-- Sub Title / END --
	-- Content --
	CASE lang
	WHEN 'english' THEN
	 n.content_en
	WHEN 'spanish' THEN
	 n.content_es
	WHEN 'french' THEN
	 n.content_fr
	WHEN 'portuguese' THEN
	 n.content_pt
	WHEN 'italian' THEN
	 n.content_it
	WHEN 'deutsch' THEN
	 n.content_de
	WHEN 'russian' THEN
	 n.content_ru
	WHEN 'korean' THEN
	 n.content_kr
	WHEN 'taiwan' THEN
	 n.content_tw
	WHEN 'chinese' THEN
	 n.content_cn
	END AS content,
	-- Content / END --
	n.`image`,
	u.ID as id_user,
	u.username,
	n.posted_by,
	s.id as id_state,
	COUNT(nc.id) AS comments,
	s.state
	FROM
	news AS n
	INNER JOIN users AS u ON(u.`ID` = n.`posted_by`)
	INNER JOIN state AS s ON(s.`id` = n.`state`)
	LEFT JOIN `news_comments` AS nc ON (nc.`id_news` = n.`id`)
	WHERE (v_id IS NULL OR n.id = v_id) AND n.state IN (1, 3)
	GROUP BY n.`id`
	ORDER BY n.`id` DESC
	limit 6;
END
;;
delimiter ;

