-- ----------------------------
-- Procedure structure for sp_sub_menu
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_sub_menu`;
delimiter ;;
CREATE PROCEDURE `sp_sub_menu`(in v_category SMAllint, IN lang VARCHAR(12), in v_active tinyint)
BEGIN
		SELECT
		`id`,
		`id_menu`,
		`id_sub_menu_category`,
		CASE lang
		WHEN 'english' THEN
		 sub_menu_en
		WHEN 'spanish' THEN
		 sub_menu_es
		WHEN 'french' THEN
		 sub_menu_fr
		WHEN 'portuguese' THEN
		 sub_menu_pt
		WHEN 'italian' THEN
		 sub_menu_it
		WHEN 'deutsch' THEN
		 sub_menu_de
		WHEN 'russian' THEN
		 sub_menu_ru
		WHEN 'korean' THEN
		 sub_menu_kr
		WHEN 'taiwan' THEN
		 sub_menu_tw
		WHEN 'chinese' THEN
		 sub_menu_cn
		END AS sub_menu,
		url,
		`new_feature`,
		`active`
		FROM `sub_menu`
		WHERE `id_sub_menu_category` = v_category AND (v_active IS NULL OR `active` = v_active);
	END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_sub_menu_category
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_sub_menu_category`;
delimiter ;;
CREATE PROCEDURE `sp_sub_menu_category`(in v_id_menu smallint, IN lang VARCHAR(12))
BEGIN
		SELECT DISTINCT
		smc.`id`,
		CASE lang
		WHEN 'english' THEN
		 smc.category_en
		WHEN 'spanish' THEN
		 smc.category_es
		WHEN 'french' THEN
		 smc.category_fr
		WHEN 'portuguese' THEN
		 smc.category_pt
		WHEN 'italian' THEN
		 smc.category_it
		WHEN 'deutsch' THEN
		 smc.category_de
		WHEN 'russian' THEN
		 smc.category_ru
		WHEN 'korean' THEN
		 smc.category_kr
		WHEN 'taiwan' THEN
		 smc.category_tw
		WHEN 'chinese' THEN
		 smc.category_cn
		END AS category
		FROM `sub_menu` AS sm
		INNER JOIN `sub_menu_category` AS smc ON (smc.`id` = sm.`id_sub_menu_category`)
		WHERE (v_id_menu IS NULL OR sm.`id_menu` = v_id_menu) AND smc.`active` = 1;
	END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_sub_menu_distinct
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_sub_menu_distinct`;
delimiter ;;
CREATE PROCEDURE `sp_sub_menu_distinct`()
BEGIN
		SELECT DISTINCT `id_menu` FROM `sub_menu` WHERE active = 1;
	END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
